import React, { useEffect, useState } from "react";
import { api } from "../api";

// Chart.js imports
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar, Pie } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [habits, setHabits] = useState([]);
  const token = localStorage.getItem("token");

  const fetchHabits = async () => {
    try {
      const res = await api.get("/habits", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHabits(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []);

  // Compute stats
  const totalHabits = habits.length;
  const totalCompleted = habits.reduce(
    (sum, habit) => sum + habit.completedDates.length,
    0
  );

  // SDG category summary
  const categories = {};
  habits.forEach((h) => {
    if (!categories[h.category]) categories[h.category] = 0;
    categories[h.category] += h.completedDates.length;
  });

  // Chart data
  const barData = {
    labels: Object.keys(categories),
    datasets: [
      {
        label: "Completed counts",
        data: Object.values(categories),
        backgroundColor: "rgba(34,197,94,0.6)",
      },
    ],
  };

  const pieData = {
    labels: Object.keys(categories),
    datasets: [
      {
        label: "Habits Distribution",
        data: Object.values(categories),
        backgroundColor: [
          "#16a34a",
          "#22c55e",
          "#4ade80",
          "#86efac",
          "#bbf7d0",
        ],
      },
    ],
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4 text-green-600">Dashboard</h2>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div className="bg-white shadow rounded p-4">
          <p className="text-gray-500">Total Habits</p>
          <p className="text-xl font-bold">{totalHabits}</p>
        </div>
        <div className="bg-white shadow rounded p-4">
          <p className="text-gray-500">Total Completed</p>
          <p className="text-xl font-bold">{totalCompleted}</p>
        </div>
      </div>

      {/* Category Chart */}
      <div className="bg-white shadow rounded p-4 mb-6">
        <h3 className="font-semibold mb-2">SDG Category Progress</h3>

        {Object.keys(categories).length === 0 ? (
          <p className="text-gray-500">No data to show yet.</p>
        ) : (
          <>
            <Bar data={barData} />
            <div className="mt-6">
              <Pie data={pieData} />
            </div>
          </>
        )}
      </div>

      {/* Habit List */}
      <div className="bg-white shadow rounded p-4">
        <h3 className="font-semibold mb-2">Recent Habits</h3>
        <ul className="space-y-2">
          {habits.slice(-5).map((h) => (
            <li key={h._id} className="flex justify-between">
              <span>{h.title}</span>
              <span className="text-gray-500">
                Completed {h.completedDates.length} times
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
