import React, { useEffect, useState } from "react";
import { api } from "../api";

export default function Habits() {
  const [habits, setHabits] = useState([]);
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("");

  const token = localStorage.getItem("token"); // login stores JWT

  const fetchHabits = async () => {
    const res = await api.get("/habits", {
      headers: { Authorization: `Bearer ${token}` }
    });
    setHabits(res.data);
  };

  const addHabit = async () => {
    if (!title || !category) return;
    await api.post("/habits", { title, category }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setTitle(""); setCategory("");
    fetchHabits();
  };

  const markComplete = async (id) => {
    await api.patch(`/habits/${id}/complete`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    fetchHabits();
  };

  useEffect(() => { fetchHabits(); }, []);

  return (
    <div>
      <h2>My SDG Habits</h2>
      <input placeholder="Habit title" value={title} onChange={e => setTitle(e.target.value)} />
      <input placeholder="SDG category" value={category} onChange={e => setCategory(e.target.value)} />
      <button onClick={addHabit}>Add Habit</button>

      <ul>
        {habits.map(h => (
          <li key={h._id}>
            {h.title} ({h.category}) - Completed: {h.completedDates.length} times
            <button onClick={() => markComplete(h._id)}>Complete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
