import React, { useState } from 'react';
import { api } from '../api';
export default function Register(){
  const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState('');
  const submit = async (e)=>{ e.preventDefault(); try{ await api.post('/auth/register',{name,email,password}); alert('Registered'); }catch(e){ alert('Register failed') } };
  return (
    <form onSubmit={submit} className="max-w-md">
      <h2 className="text-xl mb-2">Register</h2>
      <input className="border p-2 w-full mb-2" placeholder="Name" value={name} onChange={e=>setName(e.target.value)} />
      <input className="border p-2 w-full mb-2" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
      <input className="border p-2 w-full mb-2" placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
      <button className="bg-green-600 text-white px-4 py-2">Register</button>
    </form>
  )
}
