import React, { useState } from 'react';
import { api } from '../api';
export default function Login(){
  const [email,setEmail]=useState(''); const [password,setPassword]=useState('');
  const submit = async (e)=>{ e.preventDefault(); try{ const r=await api.post('/auth/login',{email,password}); localStorage.setItem('token', r.data.token); alert('Logged in'); }catch(e){ alert('Login failed') } };
  return (
    <form onSubmit={submit} className="max-w-md">
      <h2 className="text-xl mb-2">Login</h2>
      <input className="border p-2 w-full mb-2" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
      <input className="border p-2 w-full mb-2" placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
      <button className="bg-green-600 text-white px-4 py-2">Login</button>
    </form>
  )
}
